package com.example.admin.myapplicationtest;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import com.example.admin.myapplicationtest.core.MyApplication;import com.example.admin.myapplicationtest.models.Animal;import java.util.List;import io.objectbox.Box;import io.objectbox.BoxStore;public class MainActivity extends AppCompatActivity {    private BoxStore boxStore = MyApplication.getApp().getBoxStore();    Box<Animal> animalBox = boxStore.boxFor(Animal.class);    @Override    protected void onCreate(Bundle savedInstanceState) {        List<Animal> animals = animalBox.getAll();        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);    }}