package com.example.admin.myapplicationtest;import android.content.Intent;import android.support.annotation.NonNull;import android.support.design.widget.NavigationView;import android.support.design.widget.Snackbar;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.Toolbar;import android.view.MenuItem;import android.view.View;import android.widget.Button;import android.widget.TextView;import com.andremion.floatingnavigationview.FloatingNavigationView;import com.example.admin.myapplicationtest.core.MyApplication;import com.example.admin.myapplicationtest.listing.AnimalList;import com.example.admin.myapplicationtest.listing.AnimalZooJoin;import com.example.admin.myapplicationtest.listing.ZooList;import com.example.admin.myapplicationtest.models.Animal;import com.example.admin.myapplicationtest.models.Zoo;import io.objectbox.Box;import io.objectbox.BoxStore;public class MainActivity extends AppCompatActivity {    private TextView animalTV , zooTV;    private Button submit;    private FloatingNavigationView mFloatingNavigationView;    private BoxStore boxStore = MyApplication.getApp().getBoxStore();    private Box<Animal> animalBox = boxStore.boxFor(Animal.class);    Box<Animal> box = boxStore.boxFor(Animal.class);    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        getViewFromXML();        Toolbar toolbar = findViewById(R.id.toolbar);        setSupportActionBar(toolbar);        mFloatingNavigationView = findViewById(R.id.floating_navigation_view);        mFloatingNavigationView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                mFloatingNavigationView.open();            }        });        mFloatingNavigationView.setNavigationItemSelectedListener(new NavigationView.OnNavigationItemSelectedListener() {            @Override            public boolean onNavigationItemSelected(@NonNull MenuItem item) {                Snackbar.make((View) mFloatingNavigationView.getParent(), item.getTitle() + " Selected!", Snackbar.LENGTH_SHORT).show();                Intent intent;                mFloatingNavigationView.close();                switch(item.getItemId()) {                    case  R.id.nav_camera:                        intent = new Intent(getApplicationContext(), ZooList.class);                        startActivity(intent);                        break;                    case R.id.nav_gallery:                        intent = new Intent(getApplicationContext(), AnimalList.class);                        startActivity(intent);                        break;                    case R.id.nav_slideshow:                        intent = new Intent(getApplicationContext(), AnimalZooJoin.class);                        startActivity(intent);                }                return true;            }        });        submit.setOnClickListener( new View.OnClickListener(){            @Override            public void onClick(View v) {                Animal animal = new Animal();                Zoo myZoo = new Zoo();                animal.setName(animalTV.getText().toString());                myZoo.setName(zooTV.getText().toString());                animal.getZoo().setTarget(myZoo);                animalBox.put(animal);            }        });    }    public void getViewFromXML(){        animalTV = findViewById(R.id.animalrNameText);        zooTV = findViewById(R.id.zooNameText);        submit = findViewById(R.id.submit);    }    @Override    public void onBackPressed() {        if (mFloatingNavigationView.isOpened()) {            mFloatingNavigationView.close();        } else {            super.onBackPressed();        }    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        Intent intent;        switch(item.getItemId()) {            case  R.id.nav_camera:                intent = new Intent(this, ZooList.class);                startActivity(intent);                break;            case R.id.nav_gallery:                intent = new Intent(this, AnimalZooJoin.class);                startActivity(intent);                break;        }        return true;    }}