package com.example.admin.myapplicationtest.listing;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.TextView;import com.example.admin.myapplicationtest.R;import com.example.admin.myapplicationtest.core.MyApplication;import com.example.admin.myapplicationtest.models.Animal;import com.example.admin.myapplicationtest.models.Zoo;import java.util.List;import io.objectbox.Box;import io.objectbox.BoxStore;public class AnimalList extends AppCompatActivity {    TextView listText;    BoxStore boxStore = MyApplication.getApp().getBoxStore();    Box<Animal> animalBox = boxStore.boxFor(Animal.class);    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_animal_list);        listText = findViewById(R.id.animalList);        List<Animal> animals = animalBox.getAll();        for (Animal animal : animals){            listText.setText(animal.getName().toString() + "\n");        }    }}