package com.example.admin.myapplicationtest.listing;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import com.example.admin.myapplicationtest.R;import com.example.admin.myapplicationtest.core.MyApplication;import com.example.admin.myapplicationtest.models.Animal;import com.example.admin.myapplicationtest.models.Animal_;import com.example.admin.myapplicationtest.models.Zoo;import com.example.admin.myapplicationtest.models.Zoo_;import java.util.List;import io.objectbox.Box;import io.objectbox.BoxStore;import io.objectbox.query.Query;public class AnimalZooJoin extends AppCompatActivity {    private BoxStore boxStore = MyApplication.getApp().getBoxStore();    private Box<Animal> animalBox = boxStore.boxFor(Animal.class);    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_animal_zoo_join);        Query<Animal> queryBuilder = animalBox.query().equal( Animal_.id, 1L).build();        List animals = queryBuilder.find();    }}