package com.example.admin.myapplicationtest.models;import io.objectbox.annotation.Entity;import io.objectbox.annotation.Id;import io.objectbox.annotation.Transient;import io.objectbox.annotation.Unique;import io.objectbox.relation.ToOne;@Entitypublic class Books {    @Id    public long id;    @Unique    public String name;    @Transient    private int tempUsageCount;    public ToOne<Customer> customer;    public Books() {    }    public Books(long id, String name) {        this.id = id;        this.name = name;    }}